 private String getRemoteUser(XWikiContext context) {  
  String userName = context.getRequest().getHttpServletRequest()  
    .getRemoteUser();  
  if (userName != null) {  
   // only take the front of the username@domain  
   String[] elements = userName.split(&quot;@&quot;, 2);  
   userName = elements[0];  
  }  
  return userName;  
 }  

