    }  

    @Override  
    Class getConfigurationType() {  
        return ConfigurationType.class  
    }  

    @Override  
    Object getConfiguration(BaseObject object) {  
        return new ConfigurationType(object.getStringValue(&quot;property&quot;))  
    }  

    @Override  
